import '../css/App.css';
import React from 'react';
import SetTable from './SetTable';
import { connect } from 'react-redux';
//import {store} from '../index.js';

class App extends React.Component{
    handleDispatch(action, level) {
        this.props.dispatch({type: action, value: level});
    }

    screenShow(){
        if(this.props.gameStarted){
            return (
                <div>
                    <div className="gamePageButtons">
                        <div className="gamePageButton" onClick={() => this.handleDispatch("newGame")}>reset game</div>
                        <div className="gamePageButton" onClick={() => this.handleDispatch("draw3cards")}>draw 3 cards</div>
                        <div className="gameButton" onClick={() => this.handleDispatch("showHint")}>show a set</div> 
                        <div className="gameButton" onClick={() => this.handleDispatch("endGame")}>change mode</div>
                    </div>
                    <div className="deckInfo"><b>{this.props.deck.length}</b> cards left in deck</div>
                    <GameTable onTable={this.props.onTable}></GameTable>
                </div>
            )
        } else {
            return (
                <ModeButtonGroup />
            )
        }
    }

    render() {
        console.log(this.props);
        return(
            <div>
                <h1>SET Card - Go!</h1>
                <button onClick = {() => this.handleDispatch("ChooseModeAndStart", this.props.level)}>creatDeck</button>
                {this.screenShow()}
            </div>
        )
    } 
}

let mapDispatchToProps = function(dispatch, props) {
    return {
      dispatch: dispatch,
    }
}

let mapStateTpProps = function(state, props) {
    return {
        gameStarted: state.cards.gameStarted,
        level: state.cards.level,
        deck : state.cards.deck,
        tableShow : state.cards.tableShow,
        selected: state.cards.selected,
    }
}

export default connect(
    mapStateTpProps,
    mapDispatchToProps
)(App)
