import {startNewGame} from '../Controller/GameController';


function CardReducer(state={gameStarted: false, deck: [], tableShow:[], selected:[],level: "easy",}, action) {
    switch(action.type) {
        case "setModeAndStart": {
            const newState = {
                ...state,
                level: action.value,
                gameStarted: true,
            }

            return startNewGame(newState);
        }
        default:
            return state;
    }


}
export default CardReducer;
